<?php	/* ***** Mostrar Torneios ***** */	//***2016/05/21*** 	//echo $_SERVER["SERVER_NAME"];exit; 	$context = stream_context_create(array('http' => array('header'=>'Connection: close')));		// Lendo String de conexão	$file = "../config/conexao_ca.cfg";	$fh = fopen($file, 'r');	$conteudo = explode("*", fread($fh, filesize($file)));	$strconexao = trim($conteudo[0]);	$codificacao = trim($conteudo[1]);	fclose($fh);		$sqlexp1="SELECT * FROM torneios ";	$sqlexp2="WHERE ";	$sqlexp3="";		//echo "*$strconexao*$torneio_reg*";exit;	if($torneio_reg=='')	{		$torneio_reg = trim($_POST['torneio_reg']);		//echo "*$strconexao*$torneio_reg*";exit;		if($torneio_reg=='')		{			$torneio_reg = trim(htmlspecialchars($_GET['torneio_reg']));			$janela = trim(htmlspecialchars($_GET['janela']));			//echo "*$torneio_reg* *$janela*<br>";//exit;		}		//echo "*$torneio_reg*";exit;		//$torneio_reg = 30;		//echo "$torneio_reg<br>";	}		//echo "*$torneio_reg*";exit;	if($torneio_reg!='')	{		$sqlexp3=$sqlexp3 . "reg=".$torneio_reg;		//echo "$sqlexp3<br>";		$sqlexp=$sqlexp1 . $sqlexp2 . $sqlexp3;		//echo "$sqlexp<br>";	}	else 	{		if ($sqlexp3!="")		{			//$sqlexp3=substr($sqlexp3,0,strlen($sqlexp3)-5);			$sqlexp=$sqlexp1 . $sqlexp2 . $sqlexp3;		}	}  	//Conectando com o DB	$conexao=pg_connect($strconexao) or die("erro na conexão");	//Selecionando registros da Página	//$sqlexp=$sqlexp . " ORDER BY data_rec DESC";	//echo "$strconexao<br> $sqlexp<br>";exit;	$sql=pg_query($conexao,$sqlexp); // or die("Sem Resultados!");	$resultado=pg_num_rows($sql);	//echo "$resultado<br> <br>";exit;	if ($resultado<1)	{		echo "<font size='4'><br><br><center>Ainda não temos Torneios cadastrados com os requisitos pedidos!<br><br>								<a href='#' onclick='history.go(-1);return false;'>Voltar ao Formulário de Consulta!</a>							";		return;	}	else	{		$i=0;		while ($i<$resultado)		{			$lista[$i+1]=$i;			$i++;		}	}	$qtlista=count($lista);	//echo "$qtlista";exit;	$j=1;	while ($j<=$qtlista)	{		$i=$lista[$j];		$torneio_reg = pg_result($sql,$i,'reg');		$torneio_nome = trim(pg_result($sql,$i,'nome'));		$ArqTorneio = trim(pg_result($sql,$i,'arquivo'));		$datrec = trim(pg_result($sql,$i,'data_rec'));		$remetente = trim(pg_result($sql,$i,'remetente'));		//echo "<br><br>$torneio_nome  -  $ArqTorneio<br><br>";exit;		if($codificacao=='UTF-8')		{			//$torneio_nome = utf8_decode($torneio_nome);			//$remetente = utf8_decode($remetente);		}		//echo "$torneio_nome";exit;		//echo "$ArqTorneio";exit;		$j++;	}		//echo "kkkkk $j -- $torneio_nome";		//echo "sss zzz:"; echo '*'.$_SERVER['SERVER_NAME'].'*';//exit;	//$ArqTorneio = $torneio_nome;	//trim(pg_result($sql,1,'nome'));	//echo " * ($ArqTorneio) * ($datrec) *($remetente) * ($torneio_reg) *  ($janela) *<br>";exit;	//echo "../php/LerArquivoSM.php?arq=".$ArqTorneio."&torneio_reg=".$torneio_reg;	//echo "ssssssss: $_SERVER["SERVER_NAME"]";exit;	if($_SERVER["SERVER_NAME"]=='localhost' OR $_SERVER["SERVER_NAME"]=='127.0.0.1')	{		//echo " - passou 1 - $remetente";exit;		$context = file_get_contents("LerArquivoSM.php?arq=".$ArqTorneio."&dtrec=".$datrec."&remetente=".urlencode($remetente)."&torneio_reg=".$torneio_reg."&janela=".$janela);		//echo " - passou 2";	}	else	{		//$context = file_get_contents("http://www.arquivodoarbitro.esfinge.org/php/LerArquivoSM.php?arq=".$ArqTorneio."&dtrec=".$datrec."&remetente=".urlencode($remetente)."&torneio_reg=".$torneio_reg."&janela=".$janela);		$context = file_get_contents("http://www.arquivodoarbitro.esfinge.tk/php/LerArquivoSM.php?arq=".$ArqTorneio."&dtrec=".$datrec."&remetente=".urlencode($remetente)."&torneio_reg=".$torneio_reg."&janela=".$janela);	}  echo "<div style='position:absolute;width:864px;height:601px;overflow:hidden;text-align:left;padding-left:2;'>$context</div>";//	echo "<div><html><head><meta http-equiv='refresh' content='0;URL=../arquivodoarbitro/php/LerArquivoSM.php?arq=$ArqTorneio'></head><body></body></html></div>";	//echo "<div><html><head><meta http-equiv='refresh' content='0;URL=LerArquivoSM.php?arq=$ArqTorneio".&dtrec=".$datrec."&remetente=".urlencode($remetente)."&torneio_reg=".$torneio_reg."&janela=".$janela'></head><body></body></html></div>";	echo "<div><html><head><meta http-equiv='refresh' content='0;URL=LerArquivoSM.php?arq=$ArqTorneio&dtrec=$datrec&remetente=".urlencode($remetente)."&torneio_reg=.$torneio_reg&janela=$janela'></head><body></body></html></div>";	$Erro=pg_last_error($conexao);	if($Erro!="")	{echo "<br> ... Erro: $Erro ...<br><br>";}	exit;?>